import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
  },
  email: {
    type: String,
    required: true,
    unique: true,
  },
  password: { 
    type: String,
    required: true,
  },
  image:{
    type: String,
    default: "data:image/svg+xml;base64,",
  },
  address: {
    type: Object,
    default: {line1: "", line2: ""},
  },
  phone: {
    type: Number,
    default: "0000000000",
  },
  dob: {
    type: String,
    default: "not selected"
  },
  gender: {
    type: String,
    default: "Not selected"
  },
  createdAt: {
    type: Date,
    default: Date.now,
  },
  appointments: [
    {
      type: mongoose.Schema.Types.ObjectId,
      ref: "appointmentModel",
    },
  ],
  prescriptions: [
    {
      docId: {
        type: mongoose.Schema.Types.ObjectId,
        ref: "doctorModel",
        required: true,
      },
      prescriptionDate: {
        type: Date,
        required: true,
      },
      details: {
        type: String,
        required: true,
      },
    },
  ],
});


const userModel = mongoose.models.user || mongoose.model("user", userSchema);

export default userModel;